import React from "react";
import styled from "styled-components";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";

function PhotoSlider() {
  return (
    <Carousel
      autoPlay
      labels={false}
      showIndicators={false}
      showThumbs={false}
      showStatus={false}
      infiniteLoop
      interval={5000}
    >
      <Container>
        <img src="./content/background/bg 1.jpg" alt="" />
      </Container>
      <Container>
        <img src="./content/background/bg 2.jpg" alt="" />
      </Container>
      <Container>
        <img src="./content/background/bg 3.jpg" alt="" />
      </Container>
    </Carousel>
  );
}

const Container = styled.div`
  width: 100%;

  img {
    width: 100%;
    aspect-ratio: 16 / 9;
    z-index: -100;
    @media only screen and (min-width: 1200px) {
      height: 100%;
    }
  }
  @media only screen and (min-width: 1200px) {
    height: 100vh;
  }
`;

export default PhotoSlider;
