import React from "react";
import styled from "styled-components";
function Footer() {
  return (
    <Container>
      <Logo>
        <img src="./logo.png" alt="" />
      </Logo>
      <Center>
        <h3>Follow US</h3>

        <div className="icons">
          <img src="./instagram.png" alt="" />
          <img src="./fb.png" alt="" />
          <img src="./youtube.png" alt="" />
          <img src="./pinterest.png" alt="" />
        </div>
      </Center>

      <ContactUS>
        <h3>Contact US</h3>
        <div className="contact-detail">
          <p>
            Phone: <span>+91 123-456-7890</span>
          </p>
          <p>
            Email: <span>studio9.netlify.app</span>
          </p>
        </div>
      </ContactUS>
    </Container>
  );
}

const Container = styled.div`
  width: 100%;
  background: black;
  height: 273px;
  display: flex;
  align-items: center;
  justify-content: space-evenly;

  @media only screen and (max-width: 900px) {
    flex-direction: column;
    height: fit-content;
    padding: 40px 0px;
    align-items: center;
  }
`;

const Logo = styled.div`
  width: 200px;
  img {
    width: 100%;
  }

  @media only screen and (max-width: 900px) {
    margin-bottom: 40px;
  }
`;

const Center = styled.div`
  color: #fff;
  height: 130px;
  width: 178px;
  h3 {
    font-size: 26px;
    margin-bottom: 40px;
    position: relative;

    &:after {
      position: absolute;
      bottom: -20px;
      left: 2px;
      content: "";
      width: 30px;
      height: 2px;
      background-color: goldenrod;
    }
  }

  .icons {
    display: flex;
    align-items: center;
    justify-content: start;

    img {
      width: 18px;
      margin-right: 12px;
    }
  }

  @media only screen and (max-width: 900px) {
    margin-bottom: 40px;
  }
`;

const ContactUS = styled.div`
  color: #fff;
  height: 130px;
  h3 {
    font-size: 26px;
    margin-bottom: 40px;
    position: relative;

    &:after {
      position: absolute;
      bottom: -20px;
      left: 2px;
      content: "";
      width: 30px;
      height: 2px;
      background-color: goldenrod;
    }
  }

  .contact-detail {
    p {
      margin-bottom: 10px;
      font-size: 16px;
      span {
        font-size: 14px;
        margin-left: 10px;
        font-weight: 300;
      }
    }
  }
`;
export default Footer;
