import React from "react";
import styled from "styled-components";
import Footer from "./Footer";
import Header from "./Header";
import Iframe from "react-iframe";
function ContactUS() {
  return (
    <Container>
      <Header />
      <Form>
        <h3>Contact US</h3>

        <FormField>
          <InputField>
            <InputContainer>
              <label htmlFor="">Name</label>
              <input type="text" />
            </InputContainer>
            <InputContainer>
              <label htmlFor="">Email</label>
              <input type="email" />
            </InputContainer>
            <InputContainer>
              <label htmlFor="">Subject</label>
              <input type="text" />
            </InputContainer>
            <InputContainer>
              <label htmlFor="">Suggestion</label>
              <textarea type="text" />
            </InputContainer>
            <button>Get IN Touch</button>
          </InputField>
          <InfoField>
            <Info>
              <h4>Address</h4>
              <p>
                Studio Selfie Shot, First floor, Near Medisurge Hospital, 150
                Feet Ring Rd, opp. Om Nagar BRTS, Rajkot, 360004
              </p>
            </Info>
            <Info>
              <h4>Email</h4>
              <p>example@gmail.com</p>
            </Info>
            <Info>
              <h4>Call US</h4>
              <p>+91 1234567890</p>
            </Info>
          </InfoField>
        </FormField>

        <GmapField>
          <Iframe
            src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d3657.7121086189286!2d72.95140849069213!3d23.542854840078277!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2sin!4v1661061006382!5m2!1sen!2sin"
            width="1000"
            height="400"
            allowfullscreen=""
            loading="lazy"
            referrerpolicy="no-referrer-when-downgrade"
          ></Iframe>
        </GmapField>
      </Form>
      <Footer />
    </Container>
  );
}

const Container = styled.div`
  width: 100vw;
  height: fit-content;
`;

const Form = styled.form`
  margin-top: 100px;
  max-width: 1170px;
  width: 80%;
  height: fit-content;

  margin: 100px auto auto auto;
  padding: 20px;

  h3 {
    font-size: 40px;
    font-weight: 700px;

    position: relative;

    &::after {
      content: "";
      position: absolute;
      height: 2px;
      width: 30px;
      background-color: #c48f56;
      bottom: -10px;
      left: 4px;
      z-index: -1;
    }
  }
`;

const FormField = styled.div`
  margin-top: 50px;
  display: flex;

  justify-content: space-around;
  width: 90%;
  margin: 50px auto auto auto;
  padding: 10px;

  height: fit-content;

  button {
    height: 36px;
    width: 120px;
  }

  @media only screen and (max-width: 900px) {
    flex-direction: column;
  }
`;

const InputField = styled.div`
  width: 40%;

  padding: 10px;
  @media only screen and (max-width: 900px) {
    width: 100%;

    display: flex;
    flex-direction: column;
    align-items: stretch;
    justify-content: center;
  }
`;

const InputContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 20px;
  label {
    margin-bottom: 10px;
    font-weight: 700;
    font-size: 16px;
  }

  input {
    font-size: 16px;
    border: 1px solid #ccc;
    padding: 6px 10px;
    height: 20px;
    width: 90%;
    line-height: 1.3;
  }

  textarea {
    height: 100px;
    resize: vertical;
    width: 94%;
  }
`;

const InfoField = styled.div`
  width: 40%;

  padding: 10px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;

  @media only screen and (max-width: 900px) {
    flex-direction: column;
    width: 100%;
    margin-top: 50px;

    align-items: flex-start;
    justify-content: center;
  }
`;

const Info = styled.div`
  margin-bottom: 20px;
  h4 {
    font-size: 21px;
    font-weight: 400;
    line-height: 31.5px;
    margin-bottom: 16px;
  }
  p {
    font-size: 16px;
    font-weight: 300;
    line-height: 24px;
  }
`;

const GmapField = styled.div`
  width: 90%;

  display: flex;
  align-items: center;
  justify-content: center;
  margin: 50px auto auto auto;

  iframe {
    border: 1px solid lightgray;
  }
`;
export default ContactUS;
