import React, { useEffect, useState } from "react";
import { ClipLoader } from "react-spinners";
import styled from "styled-components";
import { LazyLoadImage } from "react-lazy-load-image-component";
import "react-lazy-load-image-component/src/effects/blur.css";

function Works() {
  const [currentTab, setCurrentTab] = useState({
    type: "engagement",
    size: 75,
  });

  const [loading, setLoading] = useState(false);
  const [loadSize, setLoadSize] = useState(3);
  const [seqOne, setSeqOne] = useState([]);
  const [seqTwo, setSeqTwo] = useState([]);
  const [seqThree, setSeqThree] = useState([]);
  useEffect(() => {
    setLoading(true);
    setTimeout(() => setLoading(false), 5000);
    setLoadSize(4);
  }, [currentTab]);

  const colOne = () => {
    let seq = [];

    Array(Math.floor(currentTab.size / 3))
      .fill()
      .map((item, i) => {
        seq.push(3 * (i + 1) - 2);
      });

    setSeqOne(seq);
  };

  const colTwo = () => {
    let seq = [];

    Array(Math.floor(currentTab.size / 3))
      .fill()
      .map((item, i) => {
        seq.push(3 * (i + 1) - 1);
      });

    setSeqTwo(seq);
  };
  const colThree = () => {
    let seq = [];

    Array(Math.floor(currentTab.size / 3))
      .fill()
      .map((item, i) => {
        seq.push(3 * (i + 1) - 0);
      });

    setSeqThree(seq);
  };

  useEffect(() => {
    colOne();
    colThree();
    colTwo();
  }, [currentTab]);

  console.log(seqOne, seqTwo, seqThree, loadSize);
  return (
    <Container>
      <h2>Our latest works</h2>

      <TabMenu>
        <div className="tab-one">
          <Button
            active={currentTab.type === "engagement" ? true : false}
            currentTab={currentTab.type}
            onClick={() => {
              setCurrentTab({
                type: "engagement",
                size: 75,
              });
            }}
          >
            ENGAGEMENT
          </Button>
          <Button
            active={currentTab.type === "pre_wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "pre_wedding",
                size: 25,
              })
            }
          >
            PRE WEDDING
          </Button>
        </div>

        <div className="tab-two">
          <Button
            active={currentTab.type === "wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "wedding",
                size: 18,
              })
            }
          >
            WEDDING
          </Button>
          <Button
            active={currentTab.type === "maternity_shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "maternity_shoot",
                size: 30,
              })
            }
          >
            MATERNITY SHOOT
          </Button>
          <Button
            active={currentTab.type === "baby_shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby_shoot",
                size: 26,
              })
            }
          >
            BABY SHOOT
          </Button>
        </div>
        <div className="tab-three">
          <Button
            active={currentTab.type === "baby_shower" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby_shower",
                size: 9,
              })
            }
          >
            BABY SHOWER
          </Button>
        </div>
      </TabMenu>

      <ImageGallery>
        {loading ? (
          <Loader />
        ) : (
          <div className="grid-gallery-main">
            <div className="grid-gallery-col">
              {seqOne.slice(0, loadSize).map((item, i) => (
                <div className="gallery-item">
                  <LazyLoadImage
                    src={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                    alt=""
                  />
                </div>
              ))}
            </div>
            <div className="grid-gallery-col">
              {seqTwo.slice(0, loadSize).map((item, i) => (
                <div className="gallery-item">
                  <LazyLoadImage
                    src={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                    alt=""
                  />
                </div>
              ))}
            </div>
            <div className="grid-gallery-col">
              {seqThree.slice(0, loadSize).map((item, i) => (
                <div className="gallery-item">
                  <LazyLoadImage
                    effect="blur"
                    src={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                    alt=""
                  />
                </div>
              ))}
            </div>
          </div>
        )}

        {!loading && loadSize !== currentTab.size && (
          <LoadButton onClick={() => setLoadSize(currentTab.size)}>
            Load More
          </LoadButton>
        )}
      </ImageGallery>
    </Container>
  );
}

const Loader = styled(ClipLoader)`
  position: absolute;
  top: 50px;
  left: 48%;
  transform: translate(-50%, 0);
`;

const LoadButton = styled.button`
  width: 200px;
  height: 40px;
  background-color: transparent;
  border: #bb9f00 solid 2px;
  font-size: 16px;
  color: #bb9f00;
  border-radius: 5px;
  transition: 0.3s ease-in-out;
  position: absolute;
  bottom: -20px;
  left: 50%;
  bottom: -100px;
  transform: translate(-50%, 0);

  &:hover {
    background-color: #bb9f00;
    color: #fff;
    transition: 0.3s ease-in-out;
  }
`;
const Container = styled.div`
  width: 95%;
  padding: 50px 0px;
  margin: auto;
  max-width: 1170px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;

  h2 {
    font-size: 36px;

    font-weight: 700;
    color: #161616;
    margin-bottom: 40px;
  }
`;

const TabMenu = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;

  button {
    font-size: 11px;
    color: #000;
  }

  @media only screen and (max-width: 710px) {
    flex-direction: column;
    .tab-one {
      margin-bottom: 15px;
    }

    .tab-three {
      margin-top: 15px;
    }

    button {
      font-size: 10px;
    }
  }
`;

const ImageGallery = styled.div`
  width: 90%;
  margin: 30px auto 50px auto;
  position: relative;
  .grid-gallery-main {
    display: grid;
    grid-template-columns: repeat(3, minmax(0, 1fr));
    grid-gap: 20px;

    .grid-gallery-col {
      display: grid;
      grid-template-columns: minmax(0, 1fr);
      grid-auto-rows: minmax(min-content, max-content);
      grid-gap: 20px;
      .gallery-item {
        width: 100%;
        height: 100%;
        img {
          width: 100%;
          height: 100%;
        }
      }
    }
  }
`;

const Button = styled.button`
  position: relative;
  margin-right: 20px;
  font-size: 14px;
  background-color: transparent;
  border: none;
  cursor: pointer;
  &:hover {
    color: #bb9f00;
  }

  &::after {
    position: absolute;
    z-index: -1;
    content: "";
    transform: translate(-50%, 0);
    left: 50%;
    width: 110%;
    height: 10px;
    bottom: -4px;

    opacity: 0.4;
    background: ${(props) =>
      props.active === true ? "goldenrod" : "transparent"};
  }
`;

export default Works;
