import React, { useEffect, useState } from "react";
import { ClipLoader } from "react-spinners";
import styled from "styled-components";
import { LazyLoadImage } from "react-lazy-load-image-component";
import "react-lazy-load-image-component/src/effects/blur.css";

function Works() {
  const [currentTab, setCurrentTab] = useState({
    type: "engagement",
    size: 75,
  });

  const [loading, setLoading] = useState(false);
  const [loadSize, setLoadSize] = useState(9);

  useEffect(() => {
    setLoading(true);
    setTimeout(() => setLoading(false), 5000);
    setLoadSize(9);
  }, [currentTab]);

  return (
    <Container>
      <h2>Our latest works</h2>

      <TabMenu>
        <div className="tab-one">
          <Button
            active={currentTab.type === "engagement" ? true : false}
            currentTab={currentTab.type}
            onClick={() => {
              setCurrentTab({
                type: "engagement",
                size: 75,
              });
            }}
          >
            ENGAGEMENT
          </Button>
          <Button
            active={currentTab.type === "pre_wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "pre_wedding",
                size: 29,
              })
            }
          >
            PRE WEDDING
          </Button>
          <Button
            active={currentTab.type === "wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "wedding",
                size: 18,
              })
            }
          >
            WEDDING
          </Button>
        </div>

        <div className="tab-two">
          <Button
            active={currentTab.type === "maternity_shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "maternity_shoot",
                size: 30,
              })
            }
          >
            MATERNITY SHOOT
          </Button>
          <Button
            active={currentTab.type === "baby_shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby_shoot",
                size: 26,
              })
            }
          >
            BABY SHOOT
          </Button>
          <Button
            active={currentTab.type === "baby_shower" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby_shower",
                size: 9,
              })
            }
          >
            BABY SHOWER
          </Button>
        </div>
        <div className="tab-three">
          <Button
            active={currentTab.type === "our_latest_work" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "our_latest_work",
                size: 21,
              })
            }
          >
            OUR LATEST WORK
          </Button>
        </div>
      </TabMenu>

      <ImageGallery>
        {loading ? (
          <Loader />
        ) : (
          Array(currentTab.size)
            .slice(0, loadSize)
            .fill()
            .map((item, i) => (
              <div className="image-box">
                <LazyLoadImage
                  alt={i}
                  effect="blur"
                  src={`https://ik.imagekit.io/wupg3d27d/tr:h-274,w-520/${
                    currentTab.type
                  }/${currentTab.type}_${
                    i + 1
                  }.jpg?ik-sdk-version=javascript-1.4.3&updatedAt=1662788160543`}
                  width="100%"
                />
                <div className="description">
                  <a
                    rel="noreferrer"
                    target="_blank"
                    href={`https://ik.imagekit.io/wupg3d27d/${
                      currentTab.type
                    }/${currentTab.type}_${
                      i + 1
                    }.jpg?ik-sdk-version=javascript-1.4.3&updatedAt=1662788160543`}
                  >
                    Open Image
                  </a>
                  <p></p>
                </div>
              </div>
            ))
        )}

        {!loading && loadSize !== currentTab.size && (
          <LoadButton onClick={() => setLoadSize(currentTab.size)}>
            Load More
          </LoadButton>
        )}
      </ImageGallery>
    </Container>
  );
}

const Loader = styled(ClipLoader)`
  position: absolute;
  top: 50px;
  left: 50%;
  transform: translate(-50%, 0);
`;

const LoadButton = styled.button`
  width: 200px;
  height: 40px;
  background-color: transparent;
  border: #bb9f00 solid 2px;
  font-size: 16px;
  color: #bb9f00;
  border-radius: 5px;
  transition: 0.3s ease-in-out;
  position: absolute;
  bottom: -20px;
  left: 50%;
  transform: translate(-50%, 0);

  &:hover {
    background-color: #bb9f00;
    color: #fff;
    transition: 0.3s ease-in-out;
  }
`;
const Container = styled.div`
  width: 95%;
  padding: 30px 0px;
  margin: auto;
  max-width: 1170px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;

  h2 {
    font-size: 36px;

    font-weight: 700;
    color: #161616;
    margin-bottom: 40px;
  }
`;

const TabMenu = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;

  button {
    font-size: 11px;
    color: #000;
  }

  @media only screen and (max-width: 710px) {
    flex-direction: column;
    .tab-one {
      margin-bottom: 15px;
    }

    .tab-three {
      margin-top: 15px;
    }

    button {
      font-size: 10px;
    }
  }
`;

const ImageGallery = styled.div`
  width: 100%;
  margin: 0 auto;
  padding: 50px 0px 50px 0px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(258px, 1fr));
  grid-auto-rows: minmax(min-content, max-content);
  grid-auto-flow: dense;
  grid-gap: 15px;
  position: relative;

  .image-box {
    position: relative;
    width: 100%;
    height: 100%;
    .description {
      position: absolute;
      backdrop-filter: blur(5px);
      z-index: 100;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      color: #fff;
      background: rgba(0, 0, 0, 0.4);

      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      opacity: 0;
      transition: opacity 0.5s ease-in-out;
      border-radius: 10px;

      * {
        transform: translateY(20px);
        transition: transform 0.25s;
      }

      &:hover > * {
        transform: translateY(0);
      }

      &:hover {
        opacity: 1;
        transition: opacity 0.5s ease-in-out;
      }

      a {
        font-size: 23px;
        letter-spacing: 1px;
        text-transform: uppercase;
        margin-bottom: 10px;
        border: none;
        border-bottom: 3px solid #fff;
        padding-bottom: 5px;
        background-color: transparent;
        color: white;
        font-weight: 700;
        cursor: pointer;
        text-decoration: none;
      }

      p {
        font-size: 18px;
        font-weight: 600;
      }
    }
  }

  .image-open {
    position: fixed;
    top: 0;
    z-index: 1000;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100vw;
    height: 100vh;
    background-color: black;

    img {
      width: 50%;
      height: auto;
      aspect-ratio: 16 / 9;
    }
  }

  ${this} .image-box:nth-child(7n+1) {
    grid-column: span 2;
    grid-row: span 2;

    .description {
      h3 {
        font-size: 24px;
      }
    }
  }

  ${this} .image-box img {
    height: fit-content;
    width: 100%;
    object-fit: cover;
    aspect-ratio: 16 / 9;
    border-radius: 10px;
  }

  @media only screen and (max-width: 900px) {
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    grid-auto-rows: 258px;
    grid-gap: 20px;

    ${this} .image-box:nth-child(7n+1) {
      grid-column: unset;
      grid-row: unset;
    }
  }
`;

const Button = styled.button`
  position: relative;
  margin-right: 20px;
  font-size: 14px;
  background-color: transparent;
  border: none;
  cursor: pointer;
  &:hover {
    color: #bb9f00;
  }

  &::after {
    position: absolute;
    z-index: -1;
    content: "";
    transform: translate(-50%, 0);
    left: 50%;
    width: 110%;
    height: 10px;
    bottom: -4px;

    opacity: 0.4;
    background: ${(props) =>
      props.active === true ? "goldenrod" : "transparent"};
  }
`;

export default Works;
