import React from "react";
import styled from "styled-components";

function AboutUs() {
  return (
    <Container>
      <Information>
        <h3>About Us</h3>

        <p>
          Based in Rajkot, Studio Selfie Shot is a creative insight into wedding
          photography with an exceptional experience of shooting 400+ weddings
          since 2010.
        </p>

        <p>
          We are candid and traditional specialists. We are willing to travel
          across the globe for wedding shoots.
        </p>

        <p>
          Studio Selfie Shot delivers about 250 proceeded pictures to their
          clients. They charge 50% advance while booking. Services offered:
          Candid Photography Traditional Photography Pre Wedding Shoots Wedding
          Films Albums Maternity Shoots Fashion Shoots
        </p>
      </Information>
    </Container>
  );
}

const Container = styled.div`
  height: 714px;
  width: 80%;
  max-width: 1170px;

  margin: auto;
  display: flex;
  align-items: center;
  justify-content: center;

  @media only screen and (max-width: 1095px) {
    flex-direction: column;
    height: fit-content;
    width: 90%;
  }
`;

const ImageSection = styled.div`
  height: 550px;
  width: 580px;
  img {
    height: 100%;
  }

  @media only screen and (max-width: 900px) {
    width: 90%;
    height: fit-content;

    img {
      width: 100%;
      height: auto;
    }
  }
`;

const Information = styled.div`
  height: 550px;
  width: 100%;

  background: #f0f0f0;
  display: flex;
  flex-direction: column;

  justify-content: center;
  align-items: center;
  h3 {
    font-size: 36px;
    width: 90%;
    font-weight: 700;
    color: #161616;
  }

  p {
    font-size: 16px;
    margin-top: 20px;
    line-height: 24px;
    width: 90%;
    color: #3b3b3b;
  }

  @media only screen and (max-width: 900px) {
    width: 90%;
    height: fit-content;
    padding: 20px 0;

    h3 {
      font-size: 28px;
    }

    p {
      font-size: 13px;
      line-height: 20px;
    }
  }
`;
export default AboutUs;
