import React, { useEffect, useState } from "react";
import { ClipLoader } from "react-spinners";
import styled from "styled-components";
import { LazyLoadImage } from "react-lazy-load-image-component";
import "react-lazy-load-image-component/src/effects/blur.css";
import ImageViewer from "react-simple-image-viewer";
function Works() {
  const [currentTab, setCurrentTab] = useState({
    type: "pre_wedding",
    size: 25,
  });

  const [loading, setLoading] = useState(false);
  const [loadSize, setLoadSize] = useState(3);
  const [seqOne, setSeqOne] = useState([]);
  const [seqTwo, setSeqTwo] = useState([]);
  const [seqThree, setSeqThree] = useState([]);
  const [mobile, setMobile] = useState(false);

  const [currentImage, setCurrentImage] = useState(0);
  const [isViewerOpen, setIsViewerOpen] = useState(false);

  // Load More
  useEffect(() => {
    setLoading(true);
    setTimeout(() => setLoading(false), 5000);
    setLoadSize(4);
  }, [currentTab]);

  const colOne = () => {
    let seq = [];

    Array(Math.floor(currentTab.size / 3))
      .fill()
      .map((item, i) => {
        seq.push(3 * (i + 1) - 2);
      });

    setSeqOne(seq);
  };

  const colTwo = () => {
    let seq = [];

    Array(Math.floor(currentTab.size / 3))
      .fill()
      .map((item, i) => {
        seq.push(3 * (i + 1) - 1);
      });

    setSeqTwo(seq);
  };
  const colThree = () => {
    let seq = [];

    Array(Math.floor(currentTab.size / 3))
      .fill()
      .map((item, i) => {
        seq.push(3 * (i + 1) - 0);
      });

    setSeqThree(seq);
  };

  useEffect(() => {
    colOne();
    colThree();
    colTwo();
  }, [currentTab]);

  // handle window size
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 768) {
        setMobile(true);
      } else {
        setMobile(false);
      }
    };
    window.addEventListener("resize", handleResize);
    console.log(mobile);
  });

  console.log(seqOne, seqTwo, seqThree, loadSize);
  return (
    <Container>
      <h2>Our latest works</h2>

      <TabMenu>
        <div className="tab-one">
          <Button
            active={currentTab.type === "engagement" ? true : false}
            currentTab={currentTab.type}
            onClick={() => {
              setCurrentTab({
                type: "engagement",
                size: 75,
              });
            }}
          >
            ENGAGEMENT
          </Button>
          <Button
            active={currentTab.type === "pre_wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "pre_wedding",
                size: 25,
              })
            }
          >
            PRE WEDDING
          </Button>
        </div>

        <div className="tab-two">
          <Button
            active={currentTab.type === "wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "wedding",
                size: 18,
              })
            }
          >
            WEDDING
          </Button>
          <Button
            active={currentTab.type === "maternity_shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "maternity_shoot",
                size: 30,
              })
            }
          >
            MATERNITY SHOOT
          </Button>
          <Button
            active={currentTab.type === "baby_shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby_shoot",
                size: 26,
              })
            }
          >
            BABY SHOOT
          </Button>
        </div>
        <div className="tab-three">
          <Button
            active={currentTab.type === "baby_shower" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby_shower",
                size: 9,
              })
            }
          >
            BABY SHOWER
          </Button>
        </div>
      </TabMenu>

      <ImageGallery>
        {loading ? (
          <Loader />
        ) : (
          <div className="grid-gallery-main">
            {mobile ? (
              <>
                <div className="grid-gallery-col">
                  {Array(currentTab.size)
                    .fill()
                    .slice(0, loadSize)
                    .map((item, i) => (
                      <div className="gallery-item">
                        <LazyLoadImage
                          effect="blur"
                          src={`https://ik.imagekit.io/wupg3d27d/${
                            currentTab.type
                          }/${currentTab.type}_${i + 1}.jpg`}
                          alt=""
                        />
                        <div className="description">
                          <a
                            rel="noreferrer"
                            target="_blank"
                            href={`https://ik.imagekit.io/wupg3d27d/${
                              currentTab.type
                            }/${currentTab.type}_${i + 1}.jpg`}
                          >
                            Open Image
                          </a>
                          <p></p>
                        </div>
                      </div>
                    ))}
                </div>
              </>
            ) : (
              <>
                <div className="grid-gallery-col">
                  {seqOne.slice(0, loadSize).map((item, i) => (
                    <div className="gallery-item">
                      <LazyLoadImage
                        effect="blur"
                        src={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                        alt=""
                      />
                      <div className="description">
                        <a
                          rel="noreferrer"
                          target="_blank"
                          href={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                        >
                          Open Image
                        </a>
                        <p></p>
                      </div>
                    </div>
                  ))}
                </div>
                <div className="grid-gallery-col">
                  {seqTwo.slice(0, loadSize).map((item, i) => (
                    <div className="gallery-item">
                      <LazyLoadImage
                        effect="blur"
                        src={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                        alt=""
                      />
                      <div className="description">
                        <a
                          rel="noreferrer"
                          target="_blank"
                          href={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                        >
                          Open Image
                        </a>
                        <p></p>
                      </div>
                    </div>
                  ))}
                </div>
                <div className="grid-gallery-col">
                  {seqThree.slice(0, loadSize).map((item, i) => (
                    <div className="gallery-item">
                      <LazyLoadImage
                        effect="blur"
                        src={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                        alt=""
                      />

                      <div className="description">
                        <a
                          rel="noreferrer"
                          target="_blank"
                          href={`https://ik.imagekit.io/wupg3d27d/${currentTab.type}/${currentTab.type}_${item}.jpg`}
                        >
                          Open Image
                        </a>
                        <p></p>
                      </div>
                    </div>
                  ))}
                </div>
              </>
            )}
          </div>
        )}

        {!loading && loadSize !== currentTab.size && (
          <LoadButton onClick={() => setLoadSize(currentTab.size)}>
            Load More
          </LoadButton>
        )}
      </ImageGallery>
    </Container>
  );
}

