import { collection, getDocs, query } from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { ClipLoader } from "react-spinners";
import styled from "styled-components";

function Works() {
  const [currentTab, setCurrentTab] = useState({
    type: "weddings",
    size: 16,
  });

  return (
    <Container>
      <h2>Our latest works</h2>

      <TabMenu>
        <div className="tab-one">
          <Button
            active={currentTab.type === "engagement" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "engagement",
                size: 75,
              })
            }
          >
            ENGAGEMENT
          </Button>
          <Button
            active={currentTab.type === "pre wedding" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "pre wedding",
                size: 29,
              })
            }
          >
            PRE WEDDING
          </Button>
          <Button
            active={currentTab.type === "weddings" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "weddings",
                size: 16,
              })
            }
          >
            WEDDING
          </Button>
        </div>

        <div className="tab-two">
          <Button
            active={currentTab.type === "maternity shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "maternity shoot",
                size: 31,
              })
            }
          >
            MATERNITY SHOOT
          </Button>
          <Button
            active={currentTab.type === "baby shoot" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby shoot",
                size: 33,
              })
            }
          >
            BABY SHOWER
          </Button>
          <Button
            active={currentTab.type === "baby shower" ? true : false}
            currentTab={currentTab.type}
            onClick={() =>
              setCurrentTab({
                type: "baby shower",
                size: 9,
              })
            }
          >
            BABY SHOOT
          </Button>
          <Button
            active={currentTab === "7" ? true : false}
            currentTab={currentTab.type}
            onClick={() => setCurrentTab("7")}
          >
            FAMILY
          </Button>
        </div>
      </TabMenu>

      <ImageGallery>
        {Array(currentTab.size)
          .fill()
          .map((item, i) => (
            <div className="image-box">
              <img
                src={`./content/thumbnail/${currentTab.type}/tn${i + 1}.jpg`}
                alt=""
              />
              <div className="description">
                <h3></h3>
                <p></p>
              </div>
            </div>
          ))}
      </ImageGallery>
    </Container>
  );
}

const Loader = styled(ClipLoader)`
  position: absolute;
  top: 200px;
  left: 50%;
`;

const Container = styled.div`
  width: 100%;
  padding: 30px 0px;
  margin: auto;
  max-width: 1170px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;

  h2 {
    font-size: 36px;

    font-weight: 700;
    color: #161616;
    margin-bottom: 40px;
  }
`;

const TabMenu = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;

  @media only screen and (max-width: 710px) {
    flex-direction: column;
    .tab-one {
      margin-bottom: 15px;
    }

    button {
      font-size: 14px;
    }
  }
`;

const ImageGallery = styled.div`
  width: 90%;
  margin: 0 auto;
  padding: 50px 20px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  grid-auto-rows: max-content;
  grid-auto-flow: dense;
  grid-gap: 15px;

  .image-box {
    position: relative;
    .description {
      position: absolute;
      backdrop-filter: blur(5px);
      z-index: 100;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      color: #fff;
      background: rgba(0, 0, 0, 0.4);

      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      opacity: 0;
      transition: opacity 0.5s ease-in-out;
      border-radius: 10px;

      * {
        transform: translateY(20px);
        transition: transform 0.25s;
      }

      &:hover > * {
        transform: translateY(0);
      }

      &:hover {
        opacity: 1;
        transition: opacity 0.5s ease-in-out;
      }

      h3 {
        font-size: 20px;
        letter-spacing: 1px;
        text-transform: uppercase;
        margin-bottom: 10px;
        border-bottom: 3px solid #fff;
        padding-bottom: 5px;
      }

      p {
        font-size: 18px;
        font-weight: 600;
      }
    }
  }

  ${this} .image-box:nth-child(7n+1) {
    grid-column: span 2;
    grid-row: span 2;

    .description {
      h3 {
        font-size: 24px;
      }
    }
  }

  ${this} .image-box img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    aspect-ratio: 16 / 9;
    border-radius: 10px;
  }

  @media only screen and (max-width: 900px) {
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    grid-auto-rows: 250px;
    grid-gap: 20px;

    ${this} .image-box:nth-child(7n+1) {
      grid-column: unset;
      grid-row: unset;
    }
  }
`;

const Button = styled.button`
  position: relative;
  margin-right: 20px;
  font-size: 14px;
  background-color: transparent;
  border: none;
  cursor: pointer;
  &:hover {
    color: #bb9f00;
  }

  &::after {
    position: absolute;
    z-index: -1;
    content: "";
    transform: translate(-50%, 0);
    left: 50%;
    width: 110%;
    height: 10px;
    bottom: -4px;

    opacity: 0.4;
    background: ${(props) =>
      props.active === true ? "goldenrod" : "transparent"};
  }
`;

const Description = styled.div``;
export default Works;
