import React from "react";
import styled from "styled-components";
import { LazyLoadImage } from "react-lazy-load-image-component";
import "react-lazy-load-image-component/src/effects/blur.css";
function Team() {
  return (
    <Container>
      <h2>Our Team</h2>

      <Image>
        <LazyLoadImage src="./team.jpg" alt="team" effect="blur" />
      </Image>
    </Container>
  );
}

const Container = styled.div`
  width: 100%;
  padding: 30px 0px;
  margin: auto;
  max-width: 1170px;
  display: flex;
  flex-direction: column;
  align-items: center;

  h2 {
    font-size: 36px;

    font-weight: 700;
    color: #161616;
    margin-bottom: 40px;
  }
`;

const Image = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  img {
    width: 80%;
  }
`;
export default Team;
