import { collection, getDocs, query } from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { SyncLoader } from "react-spinners";
import styled from "styled-components";
import db from "../firebase";

function Works() {
  const [currentTab, setCurrentTab] = useState("weddings");
  const [imageList, setImageList] = useState([]);
  const [loaderActive, setLoaderActive] = useState(false);
  useEffect(() => {
    const fetchdata = async () => {
      setLoaderActive(true);
      const q = query(collection(db, "thumbnail", currentTab, "list"));
      const querySnapshot = await getDocs(q);

      await setImageList(querySnapshot.docs);
      setInterval(3000, () => setLoaderActive(false));
    };
    fetchdata();
  }, [currentTab]);
  return (
    <Container>
      <h2>Our latest works</h2>

      <TabMenu>
        <div className="tab-one">
          <Button
            active={currentTab === "2" ? true : false}
            currentTab={currentTab}
            onClick={() => setCurrentTab("2")}
          >
            ENGAGEMENT
          </Button>
          <Button
            active={currentTab === "3" ? true : false}
            currentTab={currentTab}
            onClick={() => setCurrentTab("3")}
          >
            PRE WEDDING
          </Button>
          <Button
            active={currentTab === "weddings" ? true : false}
            currentTab={currentTab}
            onClick={() => setCurrentTab("weddings")}
          >
            WEDDING
          </Button>
        </div>

        <div className="tab-two">
          <Button
            active={currentTab === "5" ? true : false}
            currentTab={currentTab}
            onClick={() => setCurrentTab("5")}
          >
            MATERNITY SHOOT
          </Button>
          <Button
            active={currentTab === "baby shoot" ? true : false}
            currentTab={currentTab}
            onClick={() => setCurrentTab("baby shoot")}
          >
            BABY SHOOT
          </Button>
          <Button
            active={currentTab === "7" ? true : false}
            currentTab={currentTab}
            onClick={() => setCurrentTab("7")}
          >
            FAMILY
          </Button>
        </div>
      </TabMenu>
      <Loader color="rgb(196,143,86)" loading={loaderActive} />
      <ImageGallery>
        {loaderActive === false &&
          imageList?.map((doc) => (
            <div className="image-box">
              <img src={doc.data().image} alt="" />
              <div className="description">
                <h3>{doc.data().name}</h3>
                <p>{doc.data().location}</p>
              </div>
            </div>
          ))}
      </ImageGallery>
    </Container>
  );
}

const Loader = styled(SyncLoader)`
  position: absolute;
`;

const Container = styled.div`
  width: 100%;
  padding: 30px 0px;
  margin: auto;
  max-width: 1170px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;

  h2 {
    font-size: 36px;

    font-weight: 700;
    color: #161616;
    margin-bottom: 40px;
  }
`;

const TabMenu = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;

  @media only screen and (max-width: 710px) {
    flex-direction: column;
    .tab-one {
      margin-bottom: 15px;
    }

    button {
      font-size: 14px;
    }
  }
`;

const ImageGallery = styled.div`
  width: 90%;
  margin: 0 auto;
  padding: 50px 20px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  grid-auto-rows: max-content;
  grid-auto-flow: dense;
  grid-gap: 15px;

  .image-box {
    position: relative;
    .description {
      position: absolute;
      backdrop-filter: blur(5px);
      z-index: 100;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      color: #fff;
      background: rgba(0, 0, 0, 0.4);

      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
      opacity: 0;
      transition: opacity 0.5s ease-in-out;
      border-radius: 10px;

      * {
        transform: translateY(20px);
        transition: transform 0.25s;
      }

      &:hover > * {
        transform: translateY(0);
      }

      &:hover {
        opacity: 1;
        transition: opacity 0.5s ease-in-out;
      }

      h3 {
        font-size: 20px;
        letter-spacing: 1px;
        text-transform: uppercase;
        margin-bottom: 10px;
        border-bottom: 3px solid #fff;
        padding-bottom: 5px;
      }

      p {
        font-size: 18px;
        font-weight: 600;
      }
    }
  }

  ${this} .image-box:nth-child(7n+1) {
    grid-column: span 2;
    grid-row: span 2;

    .description {
      h3 {
        font-size: 24px;
      }
    }
  }

  ${this} .image-box img {
    width: 100%;

    object-fit: cover;
    aspect-ratio: 16 / 9;
    border-radius: 10px;
  }

  @media only screen and (max-width: 900px) {
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    grid-auto-rows: 250px;

    ${this} .image-box:nth-child(7n+1) {
      grid-column: unset;
      grid-row: unset;
    }
  }
`;

const Button = styled.button`
  position: relative;
  margin-right: 20px;
  font-size: 14px;
  background-color: transparent;
  border: none;
  cursor: pointer;
  &:hover {
    color: #bb9f00;
  }

  &::after {
    position: absolute;
    z-index: -1;
    content: "";
    width: ${(props) =>
      props.currentTab === "1"
        ? "25px"
        : props.currentTab === "2"
        ? "107px"
        : props.currentTab === "3"
        ? "110px"
        : props.currentTab === "weddings"
        ? "79px"
        : props.currentTab === "5"
        ? "142px"
        : props.currentTab === "baby shoot"
        ? "100px"
        : props.currentTab === "7"
        ? "58px"
        : "0px"};
    height: 10px;
    bottom: -4px;
    left: -5px;
    opacity: 0.4;
    background: ${(props) =>
      props.active === true ? "goldenrod" : "transparent"};
  }
`;

const Description = styled.div``;
export default Works;
